con:configuration.scriptText:
// importing the library to talk to the XML data using Java script
import com.eviware.soapui.support.XmlHolder

//storing the whole response of the create a customer in a variable called response
def response = context.testCase.getTestStepByName("Create Customer").getPropertyValue("Response")
log.info response
// change the response variable into an xml holder format
def holder = new XmlHolder(response)

//taking the tags off of the holder variable and storing the id 
def session = holder.getNodeValue("//*:id")

//storing the value into the test suite
context.testCase.testSuite.setPropertyValue("id", session)

