// We will import a library that has test runner to talk to the
// functional test suite
import com.eviware.soapui.model.testsuite.TestRunner.Status

// defining a variable to store the test runner directory
def tc = testRunner.testCase.testSuite.project.testSuites['Functional'].testCases['Script Based Execution']

// we will set up the email, firstname and lastname to new values
tc.testSteps["email first name and lastname properties"].setPropertyValue("email","blinks@gmail.com")
tc.testSteps["email first name and lastname properties"].setPropertyValue("firstname","jinks")
tc.testSteps["email first name and lastname properties"].setPropertyValue("lastname","blinks")

//to run the test with no context and no conditions
tc.run(null, false)
// to get the list of the test steps performed
def teststeplist = tc.getTestStepList()

