// We will import a library that has test runner to talk to the
// functional test suite
import com.eviware.soapui.model.testsuite.TestRunner.Status

// defining a variable to store the test runner directory
def tc = testRunner.testCase.testSuite.project.testSuites['Functional'].testCases['Script Based Execution']

// we will set up the email, firstname and lastname to new values
tc.testSteps["email first name and lastname properties"].setPropertyValue("email","blinks@gmail.com")
tc.testSteps["email first name and lastname properties"].setPropertyValue("firstname","jinks")
tc.testSteps["email first name and lastname properties"].setPropertyValue("lastname","blinks")

//to run the test with no context and no conditions
// and storing it in a variable called runner
def runner = tc.run(null, false)

//status of the test run
log.info "The Status of the Test Steps are: $runner.status"

//The time taken to run all the test steps
log.info "The time taken to run all the test steps is: $runner.timeTaken"

// The number of steps run 
log.info "The number of test steps executed are: " +runner.results.size()

// jar file jxl is used to connect between an xls file and soapui
import jxl.*

//defining empty variable names
def email, firstname, lastname

/*using the xls file path to connect with the 
 * soapui and read the data stored in the file
 */

def filepath = "C://Users//user//Desktop//links for learning//1. Assignment//Test Data.xls"

// creating an easily readable file for the soapui
File Xcel = new File(filepath)

/*excel contains workbook and sheets, so we will navigate into those
 * workbook and sheets and get the data
 */
Workbook wb = Workbook.getWorkbook(Xcel)
Sheet sh = wb.getSheet("Sheet1")

//I want the row in the sheet
def row = sh.getRows().toInteger()
//I want the column too
def col = sh.getColumns().toInteger()

//displaying the row and column
//log.info row
//log.info col

//looping through the rows
for(def i = 0; i < row; i++){
	for(def j = 0; j<col; j++){
		if(j==0){
			email=sh.getCell(j,i).getContents()
		}
		else if(j==1){
			firstname = sh.getCell(j,i).getContents()
		}
		else{
			lastname = sh.getCell(j,i).getContents()
		}
	}
	//Displaying the contents of email, firstname and lastname
	//log.info "$email $firstname $lastname"
	//adding script from status, time taken and steps executed
	// defining a variable to store the test runner directory
	def tc = testRunner.testCase.testSuite.project.testSuites['Functional'].testCases['Script Based Execution']

	// we will set up the email, firstname and lastname to new values
	tc.testSteps["email first name and lastname properties"].setPropertyValue("email","blinks@gmail.com")
	tc.testSteps["email first name and lastname properties"].setPropertyValue("firstname","jinks")
	tc.testSteps["email first name and lastname properties"].setPropertyValue("lastname","blinks")

	//to run the test with no context and no conditions
	// and storing it in a variable called runner
	def runner = tc.run(null, false)

	//status of the test run
	log.info "The Status of the Test Steps are: $runner.status"

	//The time taken to run all the test steps
	log.info "The time taken to run all the test steps is: $runner.timeTaken"

	// The number of steps run 
	log.info "The number of test steps executed are: " +runner.results.size()

}
